Trigger.script:
matches = multimatches[1] Player.lastExp = Player.exp Player.exp = tonumber(matches[3]) Player.align = tonumber(matches[4]) Player.lastGold = Player.gold Player.gold = tonumber(matches[5]) Player.lag = tonumber(matches[6]) Player.PKer = matches[7] or "" Player.last_illum = Player.illum
local exp = Player.exp - Player.lastExp if exp > 0 then Counter.addExp(exp) end local gold = Player.gold - Player.lastGold if gold > 0 then Counter.addGold(gold) end
if Player.position == "fight" then Player.position = "stand" end Player.fight.round = 0 raiseEvent("onPrompt")

